import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import Resturants from './components/Resturants';
import Categories from './components/Categories';
import Products from './components/Products';
import AllProducts from './products.json';
import Cart from './components/Cart';
import filterList from './components/filterList';
import fill from './components/filterList';




const App = () => {

  const [products, setProducts] = useState([]);
  const [selectedResturants, setSelectedResturants] = useState([]);
  const [selectedCategories, setSelectedCategories] = useState([]);
  const [cart, setCart] = useState([]);
  const [MountFlag, setMountFlag] = useState(true);
  const [Search, SearchIconWrapper, StyledInputBase] = useState([]);

  useEffect(() => {
    setProducts(filterList([], null));
    if (MountFlag) {
      axios.get('http://localhost:8080/Cart/cart/id')
        .then((response) => {
          console.log(response);
          setCart(response.data);
          setMountFlag(false)
        }).catch((err) => {
          console.log(err);
        })
    }
  }, [])

  const setResturant = (resturant) => {
    console.log(resturant);
    const resturants = [selectedResturants];
    console.log(products);
    let newArray=[];
    if(resturant=='AllProducts'){
      newArray=AllProducts
    }else{
      newArray = AllProducts.filter((element) => {
        if (element.restaurantName == resturant) {
          return element;
        }
      })
    }

    console.log(newArray)

    setProducts(newArray);
  }
  const setCategory = (category) => {
    console.log(category);
    const categories = [selectedCategories];
    console.log(products);
    let newArray=[];
    if(category=='AllProducts'){
      newArray=AllProducts
    }else{
      newArray = AllProducts.filter((element) => {
        if (element.categoryName == category) {
          return element;
        }
      })
    }

    console.log(newArray)

    setProducts(newArray);
  }

  const searchProducts=(text)=>{
    // console.log('searchtext',text);
    let searchText=text.toLocaleLowerCase()
    let filteredProducts=AllProducts.filter((product)=>{
      let productTitle=product.title.toLocaleLowerCase();
      let restaurantName=product.restaurantName.toLocaleLowerCase();
      if(productTitle.includes(searchText) || restaurantName.includes(searchText)){
        return product;
      }  
    });
    // console.log(filteredProducts);
    setProducts(filteredProducts);
  }

  const sortProducts = (method) => {
    const array = products;

    if (method === "Lowest to Highest") {
      array.sort(function (a, b) {
        console.log(a.price, b.price)
        return a.price - b.price
      })
    }
    else if (method === "Highest to Lowest") {
      array.sort(function (a, b) {
        return b.price - a.price
      })
    }
    setProducts(array);
  }

  const addToCart = (item) => {
    const productList = [...cart];
    if (!productList.includes(item)) {
      productList.push(item);
    }
    const index = productList.indexOf(item);
    productList[index].quantity++;
    setCart(productList);

    console.log(item);
    axios.post("http://localhost:8080/Cart/cart", [{
      discount: item.discount,
      price: item.price,
      quantity: item.quantity,
      title: item.title,
      url: item.url
    }]

    ).then((response) => {
      console.log(response);
    })
    .catch((error) => {
        console.log(error);
    })
  }

  const changeQuantity = (item, e) => {
    const productList = [...cart];
    console.log(item,e);
    const index = productList.indexOf(item);
    if (e === '+') {
      productList[index].quantity++;
      //update quantity of item
      axios.put('http://localhost:8080/Cart/cart',{
        id:'',
      })
      .then((Response)=>{
        console.log(Response);
      }).catch((Err)=>{
        console.log(Err);
      })
    }
    else {
      if (productList[index].quantity > 1) {
        productList[index].quantity--;
        //Remove item from Database
        axios.put('http://localhost:8080/Cart/cart',{
          id:'',
        })
        .then((Response)=>{
          console.log(Response);
        }).catch((Err)=>{
          console.log(Err);
        })
      }
      else {
        productList.splice(index, 1);
        //delete API
        axios.delete('http://localhost:8080/Cart/cart',{
          id:'',
        })
        .then((Response)=>{
          console.log(Response);
        }).catch((Err)=>{
          console.log(Err);
        })
      }
    }
    setCart(productList);
  }

  const handleClearProducts = () => {
    console.log('clear', cart);
    let CartItems=[];
    axios.get('http://localhost:8080/Cart/cart')
    .then((response)=>{
      console.log(response);
      CartItems=response.data;
    }).catch((err)=>{
      console.log(err);
    })
    CartItems.map((element)=>{
      axios.delete('http://localhost:8080/Cart/cart',{
        id:element.id,
      })
      .then((Response)=>{
        console.log(Response);
      }).catch((Err)=>{
        console.log(Err);
      })
    })

    setCart([]);
  }

  return (
    <div className="App">
      <Resturants selectedResturants={selectedResturants} setResturant={setResturant} />
      <Categories selectedCategories={selectedCategories} setCategory={setCategory} />
      <Products searchProducts={searchProducts} products={products} sortProducts={sortProducts} addToCart={addToCart} />
      <Cart handleClearProducts={handleClearProducts} products={cart} changeQuantity={changeQuantity} />

    </div>
  );

}

export default App;
